mapper.select:
select id,user_id,status as userType,point from exam_instance where is_deleted='n' and valid='y' and biz_type='partner_apply' and id>#{id}
and id=#{detailId}
limit 0,500

select b.course_type as course_type,b.status as status,count(*) as count_num from partner a,partner_course_record b,partner_station_rel c,station d,cuntao_org e where a.is_deleted='n' and b.is_deleted='n' and c.is_deleted='n' and d.is_deleted='n' and e.is_deleted='n' and a.taobao_user_id=b.partner_user_id and a.id=c.partner_id and c.station_id=d.id and d.apply_org=e.id and (e.temp_full_id_path like concat(#{orgIdPath}, '/%') or e.temp_full_id_path=#{orgIdPath})
and a.taobao_nick=#{nickName}
and a.mobile=#{phoneNum}
and a.name=#{partnerName}
and b.course_type in
#{item}
group by b.course_type,b.status

select a.name as partner_name ,a.mobile as phone_num ,a.taobao_nick as taobao_nick ,d.name as station_name ,d.id as station_id ,b.course_type as course_type ,b.status as course_status ,b.gmt_done as gmt_done ,b.partner_user_id as user_id, b.refund_status as refund_status, e.id as org_id from partner a,partner_course_record b,partner_station_rel c,station d,cuntao_org e where a.is_deleted='n' and b.is_deleted='n' and c.is_deleted='n' and d.is_deleted='n' and e.is_deleted='n' and a.taobao_user_id=b.partner_user_id and a.id=c.partner_id and c.station_id=d.id and d.apply_org=e.id and (e.temp_full_id_path like concat(#{orgIdPath}, '/%') or e.temp_full_id_path=#{orgIdPath}) and b.course_type=#{courseType}
and a.taobao_nick=#{nickName}
and a.mobile=#{phoneNum}
and a.name=#{partnerName}
and b.status=#{courseStatus}
order by a.id desc

select count(*)
from  partner a,partner_course_record b,partner_station_rel c,station d,cuntao_org e
where a.is_deleted='n' and b.is_deleted='n' and c.is_deleted='n' and d.is_deleted='n' and e.is_deleted='n'
and a.taobao_user_id=b.partner_user_id
and a.id=c.partner_id
and c.station_id=d.id
and d.apply_org=e.id
and (e.temp_full_id_path like concat(#{orgIdPath}, '/%') or e.temp_full_id_path=#{orgIdPath})
and b.course_type=#{courseType}

    and  a.taobao_nick=#{nickName}


    and  a.mobile=#{phoneNum}


    and  a.name=#{partnerName}


    and b.status=#{courseStatus}

select id,iden_num from partner where is_deleted='n' and iden_num is not null and iden_num !='' and id>#{id}
and id=#{detailId}
limit 0,500

mapper.update:
update partner
set birthday=#{birthday}
where id=#{id}

mapper.select:
select a.id,a.taobao_user_id,a.mobile,a.email,a.name from partner a,partner_station_rel b where a.is_deleted='n' and b.is_deleted='n' and a.id=b.partner_id and b.is_current='y' and b.type='TP' and b.state in('DECORATING','SERVICING') and a.id>#{id}
and a.id=#{detailId}
limit 0,500

select instance_id,question_id,question_type,seq_order,is_right,answer,point
from exam_instance_item
where is_deleted='n'
and instance_id>#{idmin}
 and instance_id<=#{idmax}     

   and instance_id=#{detailId}

 order by instance_id
