mapper.select:
select a.*
from partner_meeting a,partner_meeting_attemp b
where a.is_deleted='n'
and b.is_deleted='n'
and a.id=b.meeting_id
and b.attemper_id=#{attemperId}

  and   a.meeting_code=#{meetingCode}


     and a.gmt_start<#{gmtStartMax}     


     and a.gmt_start>=#{gmtStartMin}     


     and a.gmt_end<#{gmtEndMax}     


     and a.gmt_end>=#{gmtEndMin}     

order by a.gmt_start
 
   #{orderType}     
 
limit #{pageN},#{pageS}

  select count(*)
from partner_meeting a,partner_meeting_attemp b
where a.is_deleted='n'
and b.is_deleted='n'
and a.id=b.meeting_id
and b.attemper_id=#{attemperId}

  and   a.meeting_code=#{meetingCode}


     and a.gmt_start<#{gmtStartMax}     


     and a.gmt_start>=#{gmtStartMin}     


     and a.gmt_end<#{gmtEndMax}     


     and a.gmt_end>=#{gmtEndMin}
