mapper.select:
select a.id, a.purchase_type, a.apply_org_id, a.gmt_except_open, a.apply_work_no, a.gmt_create, b.name as org_name, a.status as status, b.full_name_path as org_full_name, a.apply_name as apply_name from peixun_purchase a,cuntao_org b where a.is_deleted='n' and b.is_deleted='n' and a.apply_org_id=b.id and (b.id=#{orgId} or b.temp_full_id_path like concat(#{orgPath}, '/%'))
and a.status=#{status}
and a.gmt_except_open>=#{gmtExceptStart}
and a.gmt_except_open<=#{gmtExceptEnd}
and a.apply_name=#{applyName}

   select 
      count(*) 
      from peixun_purchase a,cuntao_org b
      where a.is_deleted='n' and b.is_deleted='n'
      and a.apply_org_id=b.id
      and (b.id=#{orgId} or b.temp_full_id_path like concat(#{orgPath}, '/%'))
	  
	   and  a.status=#{status}
	  
	  
       and  a.gmt_except_open>=#{gmtExceptStart}
   	  
   	  
   	  and  a.gmt_except_open<=#{gmtExceptEnd}
 	  
 	  
	   and  a.apply_name=#{applyName}
