mapper.select:
select * from cuntao_county_office where county_id in 
#{countyId}
and is_deleted='n'

SELECT a.id, a.name, a.state, b.gmt_protocol_start as gmtProtocolStartDate, b.gmt_protocol_end as gmtProtocolEndDate, operate_date as operateDate, org_id as orgId, full_name_path as fullNamePath, full_id_path as fullIdPath FROM ( SELECT q1.*, q2.temp_full_id_path AS full_id_path, q2.temp_full_name_path AS full_name_path FROM cuntao_county q1,cuntao_org q2 WHERE q1.org_id = q2.id AND q1.is_deleted = 'n' AND q2.is_deleted = 'n'
and q1.state=#{condition.state}
and q1.name like CONCAT('%', #{condition.name}, '%')
and CONCAT(q2.temp_full_id_path,'/') like CONCAT(#{condition.fullIdPath}, '/%') ) AS a LEFT JOIN ( SELECT * FROM cuntao_county_gov_contract WHERE is_deleted = 'n' ) AS b ON a.id = b.county_id where 1=1 and b.gmt_protocol_end >= #{condition.protocolEndDateFrom} and b.gmt_protocol_end <= #{condition.protocolEndDateTo} order by a.id desc limit #{condition.startItem},#{condition.pageSize}

SELECT count(0) FROM ( SELECT q1.*, q2.temp_full_id_path AS full_id_path, q2.temp_full_name_path AS full_name_path FROM cuntao_county q1,cuntao_org q2 WHERE q1.org_id = q2.id AND q1.is_deleted = 'n' AND q2.is_deleted = 'n'
and q1.state=#{condition.state}
and q1.name like CONCAT('%', #{condition.name}, '%')
and CONCAT(q2.temp_full_id_path,'/') like CONCAT(#{condition.fullIdPath}, '/%') ) AS a LEFT JOIN ( SELECT * FROM cuntao_county_gov_contract WHERE is_deleted = 'n' ) AS b ON a.id = b.county_id where 1=1 and b.gmt_protocol_end >= #{condition.protocolEndDateFrom} and b.gmt_protocol_end <= #{condition.protocolEndDateTo}
