mapper.select:
select * from cuntao_county_office where county_id in 
#{countyId}
and is_deleted='n'

SELECT a.id, a.name, a.state, a.province_name as provinceName, a.city_name as cityName, a.county_name as countyName, b.gmt_start as gmtProtocolStartDate, b.gmt_end as gmtProtocolEndDate, org_id as orgId, full_name_path as fullNamePath, full_id_path as fullIdPath FROM ( SELECT q1.*, q2.temp_full_id_path AS full_id_path, q2.temp_full_name_path AS full_name_path FROM cuntao_county q1,cuntao_org q2 WHERE q1.org_id = q2.id AND q1.is_deleted = 'n' AND q2.is_deleted = 'n'
and q1.name like CONCAT('%', #{condition.name}, '%')
and CONCAT(q2.temp_full_id_path,'/') like CONCAT(#{condition.fullIdPath}, '/%') ) AS a LEFT JOIN ( SELECT * FROM cuntao_county_gov_contract WHERE is_deleted = 'n' ) AS b ON a.id = b.county_id where 1=1 and b.gmt_start >= #{condition.protocolStartDate} and b.gmt_end <= #{condition.protocolEndDate} order by a.id desc limit #{condition.startItem},#{condition.pageSize}

SELECT count(0) FROM ( SELECT q1.*, q2.temp_full_id_path AS full_id_path, q2.temp_full_name_path AS full_name_path FROM cuntao_county q1,cuntao_org q2 WHERE q1.org_id = q2.id AND q1.is_deleted = 'n' AND q2.is_deleted = 'n'
and q1.name like CONCAT('%', #{condition.name}, '%')
and CONCAT(q2.temp_full_id_path,'/') like CONCAT(#{condition.fullIdPath}, '/%') ) AS a LEFT JOIN ( SELECT * FROM cuntao_county_gov_contract WHERE is_deleted = 'n' ) AS b ON a.id = b.county_id where 1=1 and b.gmt_start >= #{condition.protocolStartDate} and b.gmt_end <= #{condition.protocolEndDate}
